mapper.insert:

INSERT INTO notice(notice_title, notice_content, notice_category, notice_regdate, notice_isshow, member_id, view_cnt, type, notice_reservation)
VALUES (#{notice_title}, #{notice_content}, #{notice_category}, now(), "Y", #{member_id}, 0, #{type},
        CASE
            WHEN #{notice_reservation} IS NOT NULL AND #{notice_reservation} != 'null' THEN #{notice_reservation}
            ELSE NULL
            END);

mapper.update:

UPDATE notice
SET notice_title = #{notice_title}, notice_content = #{notice_content}, notice_category = #{notice_category}
WHERE notice_no = #{notice_no}

mapper.delete:

UPDATE notice
SET notice_isshow = 'N'
WHERE notice_no = #{notice_no}
