mapper.select:
SELECT g.game_no, p.pay_no, g.game_title, p.pay_date, g.game_price, g.game_startdate AS game_startdate, g.game_enddate AS game_enddate, s.group_name, p.member_id FROM game g JOIN pay p ON g.game_no = p.game_no JOIN studygroup s ON g.game_no = s.game_no WHERE 1=1
AND p.member_id = #{member_id}
AND p.pay_date BETWEEN #{startDate} AND #{endDate}
